import '../css/App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Container} from 'react-bootstrap'
import React, { useState, useEffect} from "react";
import axios from 'axios'
import {AiFillEdit, AiFillCheckCircle, AiFillRightCircle} from "react-icons/ai";

function List(){
    const [tasks, setTasks] = useState([]);
    const [isVisible, setIsVisible] = useState([])

    useEffect(() => {
        getList();
    }, [])

    const getList=()=>{
        axios.get("http://localhost:8080/getList")
        .then((res) => {
        let copy = [...res.data]
        setTasks(copy);
        })
        .catch((err) => {
        console.log(err);
        })
    }

    const handleRemoveItem = (index) => {
        const updatedItems = [...tasks];
        updatedItems.splice(index, 1);
        setTasks(updatedItems);
      };

    const toggleInvisiblity = (index)=>{
        let copy = [...isVisible]
        let visible = copy[index]
        copy[index] = !visible
        setIsVisible(copy)
    }
    const showEdit = (index)=>{
        toggleInvisiblity(index)
    }
    return (
        <div className="todo-container">
        <h1 style = {{marginTop: 10, fontSize : 60}}> Ho-Do List</h1>
        <div style={{textAlign : "center",  marginTop: 20}}>
            <form action='/add' method='POST'>
            <input
            type="text"
            placeholder="Enter a task..."
            name='title'
            style={{width: "45%", fontSize: 20, height: 50}}
            />
            <input
            type="text"
            placeholder="Date(YYMMDD)"
            name='date'
            style={{width: "30%", fontSize: 20, height: 50}}
            />
            <button className = 'purpleButton' style = {{width:"10%", height:50, fontSize:20, display: 'inline-block'}} 
            type = 'submit'>Add</button>
            </form>
        </div>
        <div className='Container'>
        <ul className='list-group' style={{marginTop:20}} id="taskList">
            {tasks.map((task, index) => (
                <li className="list-group-item" style={{borderRadius: 10, display:'flex', justifyContent:'space-between'}}>
                    {!isVisible[index]?<span id="title" style={{fontSize:18}}>{task.title}</span>:
                    <form action='/edit' method='PUT'>
                        <input
                            type="text"
                            value={task.title}
                            onChange={(e)=>{
                                var title = e.target.value
                                let copy = [...tasks]
                                copy[index].title = title
                                setTasks(copy)
                            }}
                            name='title'
                            style={{width: "45%", fontSize:15, height: 18}}
                        />
                        <input
                            type="text"
                            value={task.date}
                            onChange={(e)=>{
                                var date = e.target.value
                                let copy = [...tasks]
                                copy[index].date = date
                                setTasks(copy)
                            }}
                            name='date'
                            style={{width: "35%", fontSize:15, height: 18}}
                        />
                        <button className = 'purpleButton' style = {{width:"9%", height:42, fontSize:20, display: 'inline-block'}} 
                        type = 'submit' onClick={()=>{
                            axios.put('/edit',{
                                title: tasks[index].title,
                                date: tasks[index].title,
                                _id : tasks[index]._id
                            })
                            .then()
                            .catch((err)=>{
                                console.log(err)
                            })
                        }
                        }><AiFillEdit/></button>
                    </form>}
                    <div style={{textAlign: 'right', flexDirection:'row-reverse', width:"20%"}}>
                        {!isVisible[index]&&<button className='purpleButton' style={{marginRight: 5}} data-id={task._id} type='submit' onClick={()=>{
                            showEdit(index)
                        }}>
                            <AiFillEdit/>
                        </button>}
                        <button className='purpleButton' style={{marginRight: 5}} data-id={task._id} onClick={()=>{
                            var id = task._id
                            axios.delete(`/done/${id}`)
                            .then(
                                handleRemoveItem(index)
                            )
                            .catch((err)=>{
                                console.log(err)
                            })
                        }}>
                            <AiFillCheckCircle/>
                        </button>
                        <button className='purpleButton' style={{marginRight: 5}}>
                            <AiFillRightCircle/>
                        </button>
                    </div>
                </li>
            ))}
        </ul>
        </div>
        </div>
    );
}

export default List
