import '../css/App.css';
import '../css/Calender.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Nav, Navbar, Container} from 'react-bootstrap'
import React, { useState, useEffect} from "react";
import {Route, Routes, useNavigate, useParams} from 'react-router-dom'
import axios from 'axios'
import {AiFillEdit, AiFillCheckCircle, AiFillDelete, AiFillRightCircle} from "react-icons/ai";

function List(){
    useEffect(() => {
        getList();
    }, []);
    const [tasks, setTasks] = useState([]);
    const [selectedDate, setSelectedDate] = useState(null);

    const getList=()=>{
        axios.get("http://localhost:8080/getList")
        .then((res) => {
        let copy = [...res.data];
        setTasks(copy);
        })
        .catch((err) => {
        console.log(err);
        });
    };

    return (
        <div className="todo-container">
        <h1 style = {{marginTop: 10, fontSize : 50}}> Ho-Do List</h1>
        <div style={{textAlign : "center",  marginTop: 10}}>
            <form action='/add' method='POST'>
            <input
            type="text"
            onChange={handleTaskInputChange}
            placeholder="Enter a task..."
            name='title'
            />
            <div className="date-picker-container">
                <h1 style={{marginBottom: 10}}>Date Picker</h1>
                <DatePicker
                selected={selectedDate}
                onChange={(date) => setSelectedDate(date)}
                dateFormat="yyyy-MM-dd"
                placeholderText="Select a date"
                />
                {selectedDate && <p>Selected Date: {selectedDate.toLocaleDateString()}</p>}
            </div>
            <button className = 'button' style = {{width:55, height:50, marginLeft:10, fontSize:20}} type = 'submit' onClick={handleAddTask}>Add</button>
            </form>
        </div>
        <ul style={{marginTop:20}}id="taskList">
            {tasks.map((task, index) => (
                <div>
                    <h5>{task.title}</h5>
                    <div style={{textAlign:"right"}}>
                        <button>
                            <AiFillCheckCircle/>
                        </button>
                        <button>
                            <AiFillDelete/>
                        </button>
                        <button>
                            <AiFillEdit/>
                        </button>
                        <button>
                            <AiFillRightCircle/>
                        </button>
                    </div>
                </div>

            ))}
        </ul>
        </div>
    );
}

export default List
