import '../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Nav, Navbar, Container} from 'react-bootstrap'
import React, { useState, useEffect} from "react";
import {Route, Routes, useNavigate, useParams} from 'react-router-dom'
import axios from 'axios'

function List(){
    useEffect(() => {
        getList();
    }, []);
    const [tasks, setTasks] = useState([]);
    const [taskText, setTaskText] = useState("");
    const handleTaskInputChange = (event) => {
        setTaskText(event.target.value);
    };

    const handleAddTask = () => {
        if (taskText.trim() !== "") {
        setTasks([...tasks, { text: taskText, completed: false }]);
        setTaskText("");
        }
    };

    const handleTaskCheckboxChange = (index) => {
        const updatedTasks = [...tasks];
        updatedTasks[index].completed = !updatedTasks[index].completed;
        setTasks(updatedTasks);
    };

    const getList=()=>{
        axios.get("http://localhost:8080/getList")
        .then((res) => {
        console.log(res.data)
        let copy = [...tasks];
        setTasks(copy);
        })
        .catch((err) => {
        console.log(err);
        });
    };

    return (
        <div className="todo-container">
        <h1 style = {{marginTop: 50, fontSize : 100}}> Ho-Do List</h1>
        <div style={{textAlign : "center",  marginTop: 50}}>
            <form action='/add' method='POST'>
            <input
            type="text"
            onChange={handleTaskInputChange}
            placeholder="Enter a task..."
            name='title'
            />
            <button className = 'button' style = {{width:100, height:90, marginLeft:10, fontSize:30}} type = 'submit' onClick={handleAddTask}>Add</button>
            </form>
        </div>
        <ul id="taskList">
            {tasks.map((task, index) => (
            <li
                key={index}
                className={task.completed ? "completed" : ""}
            >
                <input
                type="checkbox"
                checked={task.completed}
                onChange={() => handleTaskCheckboxChange(index)}
                />
                <span>{task.text}</span>
            </li>
            ))}
        </ul>
        </div>
    );
}

export default List
