import '../css/App.css';
import '../css/Calender.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import {Nav, Navbar, Container} from 'react-bootstrap'
import React, { useState, useEffect} from "react";
import {Route, Routes, useNavigate, useParams} from 'react-router-dom'
import axios from 'axios'
import {AiFillEdit, AiFillCheckCircle, AiFillRightCircle} from "react-icons/ai";

function List(){
    useEffect(() => {
        getList();
    }, []);
    const [tasks, setTasks] = useState([]);
    const [selectedDate, setSelectedDate] = useState(null);
    const [isVisible, setIsVisible] = useState([]);

    const getList=()=>{
        axios.get("http://localhost:8080/getList")
        .then((res) => {
        let copy = [...res.data];
        setTasks(copy);
        })
        .catch((err) => {
        console.log(err);
        });
    };

    const handleRemoveItem = (index) => {
        const updatedItems = [...tasks];
        updatedItems.splice(index, 1);
        setTasks(updatedItems);
      };

    const toggleInvisiblity = (index)=>{
        let copy = [...isVisible]
        console.log(copy)
        let visible = copy[index]
        copy[index] = !visible
        setIsVisible(copy)
    }

    return (
        <div className="todo-container">
        <h1 style = {{marginTop: 10, fontSize : 60}}> Ho-Do List</h1>
        <div style={{textAlign : "center",  marginTop: 20}}>
            <form action='/add' method='POST'>
            <input
            type="text"
            placeholder="Enter a task..."
            name='title'
            />
            <DatePicker
            selected={selectedDate}
            onChange={(date) => setSelectedDate(date)}
            dateFormat="yyyy-MM-dd"
            placeholderText="Date"
            />
            <button className = 'purpleButton' 
            style = {{width:"10%", height:50, fontSize:20, display: 'inline-block'}} 
            type = 'submit' 
            onClick={()=>{
                let copy = [...isVisible]
                copy.push(true)
                setIsVisible(copy)
            }}>Add</button>
            </form>
        </div>
        <div className='container'>
        <ul className='list-group' style={{marginTop:20}}id="taskList">
            {tasks.map((task, index) => (
                <li class="list-group-item" style={{borderRadius: 10}}>
                    {isVisible[index]&&<span id="title" style={{fontSize:18}}>{task.title}</span>}
                    <div style={{textAlign:"right", marginTop:-30}}>
                        <button className='purpleButton' style={{marginRight: 5}} data-id={task._id} onClick={()=>{
                            var id = task._id
                            axios.delete(`/done/${id}`)
                            .then(
                                handleRemoveItem(index)
                            )
                            .catch((err)=>{
                                console.log(err)
                            })
                        }}>
                            <AiFillCheckCircle/>
                        </button>
                        <button className='purpleButton' style={{marginRight: 5}} data-id={task._id} onClick={()=>{
                            toggleInvisiblity(index)
                            var id = task._id
                            axios.put(`/edit/${id}`)
                            .then(

                            )
                            .catch((err)=>{
                                console.log(err)
                            })
                        }  
                        }>
                            <AiFillEdit/>
                        </button>
                        <button className='purpleButton' style={{marginRight: 5}}>
                            <AiFillRightCircle/>
                        </button>
                    </div>
                </li>
            ))}
        </ul>
        </div>
        </div>
    );
}

export default List
